mapper.select:
SELECT *
  FROM extrapay
  WHERE ep_no = #{ep_no}

SELECT *
  FROM deduction
  WHERE ded_no = #{ded_no}

mapper.update:
UPDATE deduction SET 		
				ded_no	=	#{ded_no},
				ded_name =   #{ded_name},
				ded_order =  #{ded_order},
				calc	=	#{calc}
		WHERE ded_no = #{ded_no}

mapper.select:
SELECT *
  FROM dept
  WHERE dept_no = #{dept_no}

mapper.update:
UPDATE dept SET 		
		enabled =   'NO'
WHERE dept_no = #{dept_no}
